contracts/GameRewards.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./SudoToken.sol";

/**
 * @title GameRewards
 * @dev Manages reward distribution for CyberHack gaming platform
 */
contract GameRewards is Ownable, Pausable, ReentrancyGuard {

    SudoToken public immutable sudoToken;

    // Reward configuration
    struct RewardConfig {
        uint256 minReward;      // Minimum reward per game
        uint256 maxReward;      // Maximum reward per game  
        uint256 multiplier;     // Base multiplier (scaled by 1000)
        bool isActive;          // Whether game rewards are active
    }

    // Player statistics
    struct PlayerStats {
        uint256 totalEarned;    // Total tokens earned
        uint256 gamesPlayed;    // Total games completed
        uint256 lastRewardTime; // Last reward timestamp
        uint256 streak;         // Current daily streak
        uint256 level;          // Player level
    }

    // Game types and their reward configurations
    mapping(string => RewardConfig) public gameRewards;
    mapping(address => PlayerStats) public playerStats;
    mapping(address => uint256) public pendingRewards;

    // Platform settings
    uint256 public dailyRewardCap = 10000 * 10**18; // 10K SUDO per day per player
    uint256 public streakMultiplier = 1200; // 1.2x multiplier for streaks (scaled by 1000)
    uint256 public levelMultiplier = 50; // 5% bonus per level (scaled by 1000)

    // Anti-abuse settings
    mapping(address => uint256) public dailyRewardsClaimed;
    mapping(address => uint256) public lastRewardDate;
    uint256 public constant MIN_REWARD_INTERVAL = 30 seconds; // Minimum time between rewards

    // Events
    event RewardDistributed(address indexed player, uint256 amount, string gameType);
    event RewardsClaimed(address indexed player, uint256 amount);
    event GameRewardConfigured(string indexed gameType, uint256 minReward, uint256 maxReward);
    event PlayerLevelUp(address indexed player, uint256 newLevel);
    event StreakUpdated(address indexed player, uint256 streak);

    constructor(address _sudoToken) Ownable(_msgSender()) {
        sudoToken = SudoToken(_sudoToken);

        // Initialize default game reward configurations
        _setGameReward("password-crack", 50 * 10**18, 200 * 10**18, 1000);
        _setGameReward("network-scan", 25 * 10**18, 150 * 10**18, 1000);
        _setGameReward("sql-inject", 100 * 10**18, 500 * 10**18, 1000);
        _setGameReward("social-eng", 75 * 10**18, 300 * 10**18, 1000);
        _setGameReward("crypto-break", 200 * 10**18, 1000 * 10**18, 1000);
        _setGameReward("zero-day", 500 * 10**18, 5000 * 10**18, 1000);
    }

    /**
     * @dev Distribute rewards to player after game completion
     */
    function distributeReward(
        address player,
        uint256 baseScore,
        string calldata gameType,
        uint256 timeSpent,
        bool perfectScore
    ) external onlyOwner whenNotPaused nonReentrant {
        require(player != address(0), "Invalid player address");
        require(gameRewards[gameType].isActive, "Game type not active");
        require(
            block.timestamp >= playerStats[player].lastRewardTime+(MIN_REWARD_INTERVAL),
            "Too frequent reward claims"
        );

        // Check daily cap
        uint256 currentDate = block.timestamp / 1 days;
        if (lastRewardDate[player] != currentDate) {
            dailyRewardsClaimed[player] = 0;
            lastRewardDate[player] = currentDate;
            _updateStreak(player);
        }

        require(
            dailyRewardsClaimed[player] < dailyRewardCap,
            "Daily reward cap exceeded"
        );

        // Calculate base reward
        uint256 reward = _calculateBaseReward(baseScore, gameType, timeSpent, perfectScore);

        // Apply multipliers
        reward = _applyMultipliers(player, reward);

        // Ensure within daily cap
        if (dailyRewardsClaimed[player]+(reward) > dailyRewardCap) {
            reward = dailyRewardCap-(dailyRewardsClaimed[player]);
        }

        // Update player stats
        _updatePlayerStats(player, reward);

        // Add to pending rewards
        pendingRewards[player] = pendingRewards[player]+(reward);
        dailyRewardsClaimed[player] = dailyRewardsClaimed[player]+(reward);

        emit RewardDistributed(player, reward, gameType);
    }

    /**
     * @dev Claim pending rewards
     */
    function claimRewards() external nonReentrant whenNotPaused {
        uint256 amount = pendingRewards[_msgSender()];
        require(amount > 0, "No rewards to claim");

        pendingRewards[_msgSender()] = 0;

        // Mint tokens to player
        sudoToken.mintForAllocation(_msgSender(), amount, "gaming_rewards");

        emit RewardsClaimed(_msgSender(), amount);
    }

    /**
     * @dev Calculate base reward amount
     */
    function _calculateBaseReward(
        uint256 baseScore,
        string memory gameType,
        uint256 timeSpent,
        bool perfectScore
    ) internal view returns (uint256) {
        RewardConfig memory config = gameRewards[gameType];

        // Score-based calculation (normalize score to 0-1 range)
        uint256 scoreRatio = baseScore > 10000 ? 1000 : (baseScore * 1000) / 10000;

        // Time bonus (faster completion = higher reward)
        uint256 timeBonus = timeSpent < 60 ? 200 : timeSpent < 180 ? 150 : 100; // 2x, 1.5x, 1x

        // Perfect score bonus
        uint256 perfectBonus = perfectScore ? 150 : 100; // 1.5x for perfect

        // Calculate reward within min-max range
        uint256 rewardRange = config.maxReward-(config.minReward);
        uint256 baseReward = config.minReward + ((rewardRange * scoreRatio) / 1000);

        // Apply bonuses
        baseReward = (baseReward * timeBonus) / 100;
        baseReward = (baseReward * perfectBonus) / 100;

        return baseReward;
    }

    /**
     * @dev Apply player-specific multipliers
     */
    function _applyMultipliers(address player, uint256 baseReward) internal view returns (uint256) {
        PlayerStats memory stats = playerStats[player];
        uint256 reward = baseReward;

        // Streak multiplier
        if (stats.streak > 0) {
            uint256 streakBonus = streakMultiplier + (stats.streak * 50); // +5% per day
            reward = (reward * streakBonus) / 1000;
        }

        // Level multiplier
        if (stats.level > 1) {
            uint256 levelBonus = 1000 + (stats.level * levelMultiplier); // +5% per level
            reward = (reward * levelBonus) / 1000;
        }

        return reward;
    }

    /**
     * @dev Update player statistics
     */
    function _updatePlayerStats(address player, uint256 reward) internal {
        PlayerStats storage stats = playerStats[player];

        stats.totalEarned = stats.totalEarned+(reward);
        stats.gamesPlayed = stats.gamesPlayed+(1);
        stats.lastRewardTime = block.timestamp;

        // Level up calculation (every 10 games or 10K tokens earned)
        uint256 expectedLevel = (stats.gamesPlayed / 10) + (stats.totalEarned / (10000 * 10**18));
        if (expectedLevel > stats.level) {
            stats.level = expectedLevel;
            emit PlayerLevelUp(player, expectedLevel);
        }
    }

    /**
     * @dev Update player streak
     */
    function _updateStreak(address player) internal {
        PlayerStats storage stats = playerStats[player];
        uint256 currentDate = block.timestamp / 1 days;
        uint256 lastDate = lastRewardDate[player];

        if (lastDate == 0) {
            // First time playing
            stats.streak = 1;
        } else if (currentDate == lastDate + 1) {
            // Consecutive day
            stats.streak = stats.streak+(1);
        } else if (currentDate > lastDate + 1) {
            // Streak broken
            stats.streak = 1;
        }
        // Same day: no change to streak

        emit StreakUpdated(player, stats.streak);
    }

    /**
     * @dev Set game reward configuration (admin only)
     */
    function setGameReward(
        string calldata gameType,
        uint256 minReward,
        uint256 maxReward,
        uint256 multiplier
    ) external onlyOwner {
        _setGameReward(gameType, minReward, maxReward, multiplier);
    }

    function _setGameReward(
        string memory gameType,
        uint256 minReward,
        uint256 maxReward,
        uint256 multiplier
    ) internal {
        require(maxReward >= minReward, "Invalid reward range");
        require(multiplier >= 500 && multiplier <= 2000, "Invalid multiplier"); // 0.5x to 2x

        gameRewards[gameType] = RewardConfig({
            minReward: minReward,
            maxReward: maxReward,
            multiplier: multiplier,
            isActive: true
        });

        emit GameRewardConfigured(gameType, minReward, maxReward);
    }

    /**
     * @dev Toggle game reward status
     */
    function toggleGameReward(string calldata gameType) external onlyOwner {
        gameRewards[gameType].isActive = !gameRewards[gameType].isActive;
    }

    /**
     * @dev Update platform settings
     */
    function updatePlatformSettings(
        uint256 _dailyRewardCap,
        uint256 _streakMultiplier,
        uint256 _levelMultiplier
    ) external onlyOwner {
        dailyRewardCap = _dailyRewardCap;
        streakMultiplier = _streakMultiplier;
        levelMultiplier = _levelMultiplier;
    }

    /**
     * @dev Get player information
     */
    function getPlayerInfo(address player) external view returns (
        uint256 totalEarned,
        uint256 gamesPlayed,
        uint256 currentLevel,
        uint256 currentStreak,
        uint256 pendingReward,
        uint256 dailyClaimed
    ) {
        PlayerStats memory stats = playerStats[player];
        return (
            stats.totalEarned,
            stats.gamesPlayed,
            stats.level,
            stats.streak,
            pendingRewards[player],
            dailyRewardsClaimed[player]
        );
    }

    /**
     * @dev Emergency functions
     */
    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Emergency reward distribution (for corrections)
     */
    function emergencyReward(address player, uint256 amount) external onlyOwner {
        require(amount <= 1000 * 10**18, "Emergency reward too large"); // Max 1K per call
        pendingRewards[player] = pendingRewards[player]+(amount);
    }
}

contracts/StakingPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./SudoToken.sol";

/**
 * @title StakingPool
 * @dev Staking contract for SUDO tokens with dynamic APY and lock periods
 */
contract StakingPool is Ownable, Pausable, ReentrancyGuard {

    SudoToken public immutable sudoToken;

    // Staking position structure
    struct StakingPosition {
        uint256 amount;           // Staked amount
        uint256 startTime;        // Stake start timestamp
        uint256 lastRewardTime;   // Last reward calculation time
        uint256 lockPeriod;       // Lock period in seconds
        uint256 apy;              // APY at time of staking (scaled by 10000)
        bool isActive;            // Whether position is active
    }

    // User staking data
    mapping(address => StakingPosition[]) public userPositions;
    mapping(address => uint256) public pendingRewards;

    // Pool statistics
    uint256 public totalStaked;
    uint256 public totalRewardsPaid;
    uint256 public totalStakers;
    mapping(address => bool) public hasStaked; // Track unique stakers

    // Pool configuration
    uint256 public currentAPY = 1570; // 15.7% APY (scaled by 100)
    uint256 public minStakeAmount = 10 * 10**18; // Minimum 10 SUDO
    uint256 public maxStakeAmount = 1000000 * 10**18; // Maximum 1M SUDO per position
    uint256 public minLockPeriod = 7 days; // Minimum lock period
    uint256 public maxLockPeriod = 365 days; // Maximum lock period

    // Tier-based APY bonuses
    struct StakingTier {
        uint256 threshold;  // Minimum stake amount for tier
        uint256 bonus;      // Bonus APY in basis points (scaled by 100)
    }

    StakingTier[] public stakingTiers;

    // Emergency settings
    bool public emergencyUnstakeEnabled = false;
    uint256 public emergencyUnstakeFee = 1000; // 10% fee (scaled by 100)

    // Events
    event Staked(address indexed user, uint256 amount, uint256 lockPeriod, uint256 apy);
    event Unstaked(address indexed user, uint256 positionId, uint256 amount);
    event RewardsClaimed(address indexed user, uint256 amount);
    event APYUpdated(uint256 oldAPY, uint256 newAPY);
    event TierAdded(uint256 threshold, uint256 bonus);
    event EmergencyUnstake(address indexed user, uint256 amount, uint256 fee);

    constructor(address _sudoToken) Ownable(_msgSender()) {
        sudoToken = SudoToken(_sudoToken);

        // Initialize staking tiers
        stakingTiers.push(StakingTier(100 * 10**18, 100));    // 100+ SUDO: +1% APY
        stakingTiers.push(StakingTier(1000 * 10**18, 200));   // 1K+ SUDO: +2% APY
        stakingTiers.push(StakingTier(10000 * 10**18, 300));  // 10K+ SUDO: +3% APY
        stakingTiers.push(StakingTier(100000 * 10**18, 500)); // 100K+ SUDO: +5% APY
    }

    /**
     * @dev Stake SUDO tokens
     */
    function stake(uint256 amount, uint256 lockPeriod) external nonReentrant whenNotPaused {
        require(amount >= minStakeAmount, "Amount below minimum");
        require(amount <= maxStakeAmount, "Amount above maximum");
        require(lockPeriod >= minLockPeriod && lockPeriod <= maxLockPeriod, "Invalid lock period");

        // Transfer tokens to contract
        sudoToken.transferFrom(_msgSender(), address(this), amount);

        // Calculate effective APY with bonuses
        uint256 effectiveAPY = _calculateEffectiveAPY(amount, lockPeriod);

        // Create staking position
        userPositions[_msgSender()].push(StakingPosition({
            amount: amount,
            startTime: block.timestamp,
            lastRewardTime: block.timestamp,
            lockPeriod: lockPeriod,
            apy: effectiveAPY,
            isActive: true
        }));

        // Update pool statistics
        totalStaked = totalStaked+(amount);
        if (!hasStaked[_msgSender()]) {
            hasStaked[_msgSender()] = true;
            totalStakers = totalStakers+(1);
        }

        emit Staked(_msgSender(), amount, lockPeriod, effectiveAPY);
    }

    /**
     * @dev Unstake tokens (only after lock period)
     */
    function unstake(uint256 positionId) external nonReentrant {
        require(positionId < userPositions[_msgSender()].length, "Invalid position ID");

        StakingPosition storage position = userPositions[_msgSender()][positionId];
        require(position.isActive, "Position not active");
        require(
            block.timestamp >= position.startTime+(position.lockPeriod),
            "Lock period not expired"
        );

        uint256 amount = position.amount;

        // Calculate and add pending rewards
        uint256 rewards = _calculateRewards(_msgSender(), positionId);
        if (rewards > 0) {
            pendingRewards[_msgSender()] = pendingRewards[_msgSender()]+(rewards);
        }

        // Deactivate position
        position.isActive = false;
        totalStaked = totalStaked - amount;

        // Transfer tokens back to user
        sudoToken.transfer(_msgSender(), amount);

        emit Unstaked(_msgSender(), positionId, amount);
    }

    /**
     * @dev Emergency unstake with penalty (if enabled)
     */
    function emergencyUnstake(uint256 positionId) external nonReentrant {
        require(emergencyUnstakeEnabled, "Emergency unstake not enabled");
        require(positionId < userPositions[_msgSender()].length, "Invalid position ID");

        StakingPosition storage position = userPositions[_msgSender()][positionId];
        require(position.isActive, "Position not active");

        uint256 amount = position.amount;
        uint256 fee = (amount * emergencyUnstakeFee) / 10000;
        uint256 userAmount = amount - fee;

        // Deactivate position
        position.isActive = false;
        totalStaked = totalStaked - amount;

        // Transfer tokens (minus fee) back to user
        sudoToken.transfer(_msgSender(), userAmount);

        // Fee goes to treasury (keep in contract for now)
        emit EmergencyUnstake(_msgSender(), userAmount, fee);
    }

    /**
     * @dev Claim pending rewards
     */
    function claimRewards() external nonReentrant {
        _updateAllRewards(_msgSender());

        uint256 rewards = pendingRewards[_msgSender()];
        require(rewards > 0, "No rewards to claim");

        pendingRewards[_msgSender()] = 0;
        totalRewardsPaid = totalRewardsPaid+(rewards);

        // Mint reward tokens
        sudoToken.mintForAllocation(_msgSender(), rewards, "staking_rewards");

        emit RewardsClaimed(_msgSender(), rewards);
    }

    /**
     * @dev Calculate effective APY with bonuses
     */
    function _calculateEffectiveAPY(uint256 amount, uint256 lockPeriod) internal view returns (uint256) {
        uint256 effectiveAPY = currentAPY;

        // Tier-based bonus
        for (uint256 i = stakingTiers.length; i > 0; i--) {
            if (amount >= stakingTiers[i-1].threshold) {
                effectiveAPY = effectiveAPY+(stakingTiers[i-1].bonus);
                break;
            }
        }

        // Lock period bonus (longer lock = higher APY)
        if (lockPeriod >= 365 days) {
            effectiveAPY = effectiveAPY+(500); // +5% for 1 year lock
        } else if (lockPeriod >= 180 days) {
            effectiveAPY = effectiveAPY+(300); // +3% for 6 month lock
        } else if (lockPeriod >= 90 days) {
            effectiveAPY = effectiveAPY+(150); // +1.5% for 3 month lock
        }

        return effectiveAPY;
    }

    /**
     * @dev Calculate rewards for a specific position
     */
    function _calculateRewards(address user, uint256 positionId) internal view returns (uint256) {
        StakingPosition memory position = userPositions[user][positionId];
        if (!position.isActive) return 0;

        uint256 timeStaked = block.timestamp - position.lastRewardTime;
        uint256 yearlyReward = (position.amount * position.apy) / 10000;
        uint256 reward = (yearlyReward * timeStaked) / 365 days;

        return reward;
    }

    /**
     * @dev Update rewards for all user positions
     */
    function _updateAllRewards(address user) internal {
        StakingPosition[] storage positions = userPositions[user];

        for (uint256 i = 0; i < positions.length; i++) {
            if (positions[i].isActive) {
                uint256 rewards = _calculateRewards(user, i);
                if (rewards > 0) {
                    pendingRewards[user] = pendingRewards[user]+(rewards);
                    positions[i].lastRewardTime = block.timestamp;
                }
            }
        }
    }

    /**
     * @dev Get user's total staked amount and pending rewards
     */
    function getUserInfo(address user) external view returns (
        uint256 totalStakedAmount,
        uint256 totalPendingRewards,
        uint256 activePositions
    ) {
        StakingPosition[] memory positions = userPositions[user];
        uint256 pending = pendingRewards[user];

        for (uint256 i = 0; i < positions.length; i++) {
            if (positions[i].isActive) {
                totalStakedAmount = totalStakedAmount+(positions[i].amount);
                pending = pending+(_calculateRewards(user, i));
                activePositions++;
            }
        }

        return (totalStakedAmount, pending, activePositions);
    }

    /**
     * @dev Get user's staking positions
     */
    function getUserPositions(address user) external view returns (
        uint256[] memory amounts,
        uint256[] memory startTimes,
        uint256[] memory lockPeriods,
        uint256[] memory apys,
        bool[] memory isActive
    ) {
        StakingPosition[] memory positions = userPositions[user];
        uint256 length = positions.length;

        amounts = new uint256[](length);
        startTimes = new uint256[](length);
        lockPeriods = new uint256[](length);
        apys = new uint256[](length);
        isActive = new bool[](length);

        for (uint256 i = 0; i < length; i++) {
            amounts[i] = positions[i].amount;
            startTimes[i] = positions[i].startTime;
            lockPeriods[i] = positions[i].lockPeriod;
            apys[i] = positions[i].apy;
            isActive[i] = positions[i].isActive;
        }
    }

    /**
     * @dev Admin functions
     */
    function updateAPY(uint256 newAPY) external onlyOwner {
        require(newAPY >= 100 && newAPY <= 5000, "APY out of range"); // 1% to 50%

        uint256 oldAPY = currentAPY;
        currentAPY = newAPY;

        emit APYUpdated(oldAPY, newAPY);
    }

    function updateStakeAmounts(uint256 _minStakeAmount, uint256 _maxStakeAmount) external onlyOwner {
        require(_minStakeAmount > 0 && _maxStakeAmount > _minStakeAmount, "Invalid amounts");
        minStakeAmount = _minStakeAmount;
        maxStakeAmount = _maxStakeAmount;
    }

    function updateLockPeriods(uint256 _minLockPeriod, uint256 _maxLockPeriod) external onlyOwner {
        require(_minLockPeriod > 0 && _maxLockPeriod > _minLockPeriod, "Invalid periods");
        minLockPeriod = _minLockPeriod;
        maxLockPeriod = _maxLockPeriod;
    }

    function addStakingTier(uint256 threshold, uint256 bonus) external onlyOwner {
        require(threshold > 0 && bonus > 0 && bonus <= 1000, "Invalid tier parameters");
        stakingTiers.push(StakingTier(threshold, bonus));
        emit TierAdded(threshold, bonus);
    }

    function toggleEmergencyUnstake() external onlyOwner {
        emergencyUnstakeEnabled = !emergencyUnstakeEnabled;
    }

    function updateEmergencyUnstakeFee(uint256 fee) external onlyOwner {
        require(fee <= 2000, "Fee too high"); // Max 20%
        emergencyUnstakeFee = fee;
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Get pool statistics
     */
    function getPoolStats() external view returns (
        uint256 _totalStaked,
        uint256 _totalRewardsPaid,
        uint256 _totalStakers,
        uint256 _currentAPY
    ) {
        return (totalStaked, totalRewardsPaid, totalStakers, currentAPY);
    }
}

contracts/SudoToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title SudoToken
 * @dev POL-based ERC20 token for CyberHack gaming platform
 * Features: Minting, burning, pausing, and gaming integration
 */
contract SudoToken is ERC20, ERC20Burnable, Pausable, Ownable, ReentrancyGuard {
    // Maximum supply cap (100 million tokens)
    uint256 public constant MAX_SUPPLY = 100_000_000 * 10**18;

    // Gaming contract addresses that can mint tokens
    mapping(address => bool) public authorizedMinters;

    // Token allocation tracking
    mapping(string => uint256) public allocationUsed; // allocation name => amount used
    mapping(string => uint256) public allocationCap;  // allocation name => maximum cap

    // Events
    event MinterAuthorized(address indexed minter, bool authorized);
    event AllocationSet(string indexed allocation, uint256 cap);
    event TokensMinted(address indexed to, uint256 amount, string allocation);

    constructor() ERC20("CyberHack Sudo Token", "SUDO") Ownable(_msgSender()) {
        // Set initial allocations
        allocationCap["gaming_rewards"] = 40_000_000 * 10**18;     // 40% for gaming rewards
        allocationCap["staking_rewards"] = 20_000_000 * 10**18;    // 20% for staking rewards  
        allocationCap["team"] = 15_000_000 * 10**18;               // 15% for team
        allocationCap["treasury"] = 15_000_000 * 10**18;           // 15% for treasury
        allocationCap["liquidity"] = 10_000_000 * 10**18;          // 10% for liquidity

        // Mint initial treasury allocation to contract owner
        _mint(owner(), 5_000_000 * 10**18); // 5M initial mint for operations
        allocationUsed["treasury"] = 5_000_000 * 10**18;
    }

    /**
     * @dev Authorize or revoke minting permissions for gaming contracts
     */
    function setAuthorizedMinter(address minter, bool authorized) external onlyOwner {
        authorizedMinters[minter] = authorized;
        emit MinterAuthorized(minter, authorized);
    }

    /**
     * @dev Set allocation cap for a specific purpose
     */
    function setAllocationCap(string memory allocation, uint256 cap) external onlyOwner {
        require(cap <= MAX_SUPPLY, "Cap exceeds maximum supply");
        allocationCap[allocation] = cap;
        emit AllocationSet(allocation, cap);
    }

    /**
     * @dev Mint tokens for specific allocation (only authorized minters)
     */
    function mintForAllocation(
        address to, 
        uint256 amount, 
        string memory allocation
    ) external nonReentrant {
        require(authorizedMinters[_msgSender()], "Not authorized to mint");
        require(to != address(0), "Cannot mint to zero address");
        require(amount > 0, "Amount must be greater than zero");

        // Check allocation limits
        require(
            allocationUsed[allocation] + amount <= allocationCap[allocation],
            "Allocation cap exceeded"
        );

        // Check total supply limit
        require(
            totalSupply() + amount <= MAX_SUPPLY,
            "Maximum supply exceeded"
        );

        allocationUsed[allocation] += amount;
        _mint(to, amount);

        emit TokensMinted(to, amount, allocation);
    }

    /**
     * @dev Emergency mint function for owner (with strict limits)
     */
    function emergencyMint(address to, uint256 amount) external onlyOwner {
        require(totalSupply() + amount <= MAX_SUPPLY, "Maximum supply exceeded");
        require(amount <= 1_000_000 * 10**18, "Emergency mint too large"); // Max 1M per call

        _mint(to, amount);
    }

    /**
     * @dev Pause token transfers (emergency function)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause token transfers
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Get remaining allocation for a specific purpose
     */
    function getRemainingAllocation(string memory allocation) external view returns (uint256) {
        return allocationCap[allocation] - allocationUsed[allocation];
    }

    /**
     * @dev Bulk transfer function for airdrops (gas optimization)
     */
    function bulkTransfer(
        address[] calldata recipients, 
        uint256[] calldata amounts
    ) external nonReentrant {
        require(recipients.length == amounts.length, "Arrays length mismatch");
        require(recipients.length <= 200, "Too many recipients"); // Gas limit protection

        for (uint256 i = 0; i < recipients.length; i++) {
            require(recipients[i] != address(0), "Invalid recipient");
            require(amounts[i] > 0, "Invalid amount");
            _transfer(_msgSender(), recipients[i], amounts[i]);
        }
    }

    /**
     * @dev Override _update to add pause functionality
     */
    function _update(
        address from,
        address to,
        uint256 value
    ) internal override whenNotPaused {
        super._update(from, to, value);
    }

    /**
     * @dev Recover accidentally sent ERC20 tokens (not SUDO)
     */
    function recoverERC20(address tokenAddress, uint256 amount) external onlyOwner {
        require(tokenAddress != address(this), "Cannot recover SUDO tokens");
        IERC20(tokenAddress).transfer(owner(), amount);
    }

    /**
     * @dev Get token information for frontend
     */
    function getTokenInfo() external view returns (
        string memory name_,
        string memory symbol_,
        uint8 decimals_,
        uint256 totalSupply_,
        uint256 maxSupply_,
        bool paused_
    ) {
        return (
            name(),
            symbol(),
            decimals(),
            totalSupply(),
            MAX_SUPPLY,
            paused()
        );
    }
}

